import Phaser from 'phaser'
import GameOver from './GameOver'

// SET GAMESTATE
const GameState = {
	Paused: 'paused',
    Running: 'running',
    PlayerWon: 'player-won',
    AIWon: 'ai-won',
	GameOver: 'game-over'
}

// SET BACKGROUND
const createBGLoop = (scene, totalWidth, texture, scrollFactor) => {
	const w = scene.textures.get(texture).getSourceImage().width
	const count = Math.ceil(totalWidth / w) * scrollFactor

	let x = 0
	for (let i = 0; i < count; ++i) {
		const m = scene.add.image(x, scene.scale.height, texture)
			.setOrigin(0, 1)
			.setScrollFactor(scrollFactor)

		x += m.width
	}
}

// SET VARIABLES
var floors
var spikes
var coins

var balance = 0
var balanceText

var nextHit


// MAIN GAME

export default class ParallaxScene extends Phaser.Scene
{
	init()
	{
		this.gameState = GameState.Paused
		this.cursors = this.input.keyboard.createCursorKeys()
	}

	create()
	{	
		// CAMERA
		this.cam = this.cameras.main

		// BACKGROUND
		this.cam.setBounds(0, 0, 19200, 1080);
        const width = this.scale.width
        const height = this.scale.height
        const totalWidth = width * width
    
        this.add.image(width * 0.5, height * 0.5, 'sky').setScrollFactor(0)
    
		createBGLoop(this, totalWidth, 'mountains', 0.25)
        createBGLoop(this, totalWidth, 'plateau', 0.5)
        createBGLoop(this, totalWidth, 'ground', 1)
        createBGLoop(this, totalWidth, 'plants', 1.25)



		// PLAYER
		this.player = this.physics.add.sprite(600,800,"player")
		this.player.scaleX = 2;
        this.player.scaleY = this.player.scaleX;

		// WORLD COMPONENTS
		floors = this.physics.add.staticGroup()
		this.physics.add.collider(floors, this.player)

		floors.create(0, 925, 'floor')
		floors.setVisible(false)
		

		// SPRITES
		spikes = this.physics.add.staticGroup()
		this.physics.add.overlap(this.player, spikes, this.handleSpike, null, this)

		coins = this.physics.add.staticGroup()
		this.physics.add.overlap(this.player, coins, this.collectCoin, null, this)
		



		// MISC FUNCTIONS
		this.createAnimations()

		// CREATE WORLD
		this.createWorld()

		// ADD LABELS
		balanceText = this.add.text(16, 16, "Bitcoin 0", {fontSize:"64px",fill: "#000"}).setScrollFactor(0)

		this.cam.fadeIn(1000, 0, 0, 0)
	}



	update()
	{
		this.handleSideScroll()
		this.handleUserInput()
		this.checkBalance()
		this.checkAlive()
	}

	handleUserInput() {
        const speed = 500
        if (this.cursors.left.isDown) {
            this.player.flipX = true
            this.player.setVelocityX(-speed)
            this.player.play('player-walk', true)
        } else if (this.cursors.right.isDown) {
            this.player.flipX = false
            this.player.setVelocityX(speed)
            this.player.play('player-walk', true)
        } else {
            this.player.setVelocityX(0)
            this.player.play('player-idle', true)
        }
        if(this.cursors.space.isDown && this.player.body.blocked.down) {
            this.player.setVelocityY(-600)
        }
    }

	handleSideScroll() {
		const scrollSpeed = 3
		this.cam.scrollX += scrollSpeed
	}

	createAnimations() {
        // Create Animations
        this.anims.create({
            key: "player-walk",
            frames: this.anims.generateFrameNames('player', {start: 1, end: 4, prefix: 'penguin_walk0', suffix: '.png'}),
            frameRate: 10,
            repeat: -1
        });

        this.anims.create({
            key: "player-idle",
            frames: [{key: 'player', frame: 'penguin_walk01.png'}]
        });
    }

	createWorld() {
		spikes.create(1000, 900, 'spike')
		spikes.create(2000, 900, 'spike')
		//coins.create(1050,900, 'coin')
	}

	handleSpike() {
		if(nextHit>this.time.now){
			return
		} else {
			balance -= 0.1
			this.player.setVelocityY(-800)
			//this.sound.play(OhNonononono)
		}
		nextHit = this.time.now + 500
	}

	collectCoin() {
		balance += 0.1
		//this.sound.play(Mmmmhmm)
	}

	checkBalance() {
		balance = Phaser.Math.RoundTo(balance, -1)
		balanceText.setText("Bitcoin: " + balance)
	}

	checkAlive() {
		if (!this.cam.worldView.contains(this.player.x, this.player.y) && this.time.now > 5000) {
			this.gameState = GameState.GameOver
			this.cam.fadeOut(500, 0, 0, 0)
			this.scene.stop(ParallaxScene)
			this.scene.start(GameOver, {balance: balance})
		  }

	}




}
